"use strict";
(function() {
	$.fn.extend({
		sliderFun: function() {
			$(this).change(function(val) {
				var thisVal = val.hasOwnProperty("value") ? val.value.newValue : val.target.defaultValue
				$(this).parents(".item").css("margin-bottom", thisVal + "px");
			});
			$(this).trigger("change");
			
			console.log($(this).val());
		},
		getVal: function() {
			return this.getValue();
		},
		initSlider: function() {
			$(this).bootstrapSlider();
			var a = this.sliderFun();
			
		}
	})
})(jQuery);